body.codesample:
src_compile() {
    if [ -f Makefile ] || [ -f GNUmakefile ] || [ -f makefile ]; then
        emake || die "emake failed"
    fi
}

src_compile() { use sparc && filter-flags -fomit-frame-pointer append-ldflags -Wl,-z,now
econf \ $(use_enable ssl ) \ $(use_enable perl perlinterp )
emake || die "Make failed!" }
